mapper.select:
SELECT count(id)
FROM detection as d


    d.detected_object_id = #{detectedObjectId}


    AND (
    d.appeared_time BETWEEN #{startTime} AND #{endTime}
    OR d.discovered_time BETWEEN #{startTime} AND #{endTime}
    )

SELECT v.id, d.thumbnail_url, v.summary, d.appeared_time, d.discovered_time
FROM detection d
INNER JOIN video v on d.video_id = v.id


    d.detected_object_id = #{detectedObjectId}


    AND (
    d.appeared_time BETWEEN #{startTime} AND #{endTime}
    OR d.discovered_time BETWEEN #{startTime} AND #{endTime}
    )


ORDER BY d.appeared_time ASC
LIMIT #{pageSize}
OFFSET #{offset}

SELECT v.id, c.latitude, c.longitude
FROM detection d
INNER JOIN camera c on c.id = d.camera_id
INNER JOIN video v on v.id = d.video_id


    d.detected_object_id = #{detectedObjectId}


    AND (
    d.appeared_time BETWEEN #{startTime} AND #{endTime}
    OR d.discovered_time BETWEEN #{startTime} AND #{endTime}
    )


ORDER BY d.appeared_time ASC
